mapper.select:
SELECT *
FROM attendence
WHERE user_id = #{userId}
	AND date = #{date}

SELECT *
FROM attendance
WHERE user_id = #{userId}
	AND YEAR(date) = #{year}
	AND MONTH(date) = #{month}

mapper.delete:
DELETE
FROM attendance
WHERE
	user_id = #{userId}
	AND date = #{date}

mapper.select:
SELECT * 
FROM monthly_attendance_req 
WHERE user_id = #{userId} 
	AND target_year_month = #{targetYearMonth}

SELECT *
FROM monthly_attendance_req 
WHERE user_id = #{userId} 
	AND target_year_month = #{targetYearMonth}
